import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import SimplePeer from "simple-peer";
import "bootstrap/dist/css/bootstrap.min.css";

const socket = io("http://localhost:5000");

function App() {
  const [partnerId, setPartnerId] = useState(null);
  const [searching, setSearching] = useState(false);
  const [interests, setInterests] = useState("");
  const [commonInterests, setCommonInterests] = useState([]);
  const [stream, setStream] = useState(null);
  const [partnerStream, setPartnerStream] = useState(null);
  // Capture our own socket ID.
  const [myId, setMyId] = useState("");

  const userVideo = useRef();
  const partnerVideo = useRef();
  const peerRef = useRef(null);

  useEffect(() => {
    // Capture our socket ID when connected.
    socket.on("connect", () => {
      setMyId(socket.id);
    });

    socket.on("partnerFound", ({ partnerId, commonInterests }) => {
      setPartnerId(partnerId);
      setCommonInterests(commonInterests);
      setSearching(false);
      // Do not start video chat immediately; we'll do so once both IDs are set.
    });

    socket.on("signal", ({ signal, from }) => {
      if (peerRef.current) {
        peerRef.current.signal(signal);
      }
    });

    socket.on("partnerDisconnected", () => {
      setPartnerId(null);
      setCommonInterests([]);
      if (peerRef.current) {
        peerRef.current.destroy();
        peerRef.current = null;
      }
      setPartnerStream(null);
      alert("Your chat partner has disconnected.");
    });

    return () => {
      socket.off("connect");
      socket.off("partnerFound");
      socket.off("signal");
      socket.off("partnerDisconnected");
    };
  }, []);

  // Once both our socket ID and the partner's ID are available, start the video chat.
  useEffect(() => {
    if (partnerId && myId && !peerRef.current) {
      // Determine the initiator by comparing socket IDs.
      const initiator = myId < partnerId;
      startVideoChat(initiator);
    }
  }, [partnerId, myId]);

  const findPartner = () => {
    if (!interests.trim()) {
      alert("Please enter at least one interest!");
      return;
    }
    setSearching(true);
    const interestList = interests.split(",").map((i) => i.trim().toLowerCase());
    socket.emit("findPartner", interestList);
  };

  // Create the video chat connection using the initiator flag.
  const startVideoChat = async (initiator) => {
    try {
      const userStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(userStream);
      if (userVideo.current) {
        userVideo.current.srcObject = userStream;
      }
      const peer = new SimplePeer({
        initiator, // Dynamically set based on our socket ID vs. partner's ID.
        trickle: false,
        stream: userStream,
      });
      peer.on("signal", (signal) => {
        socket.emit("signal", { partnerId, signal });
      });
      peer.on("stream", (remoteStream) => {
        setPartnerStream(remoteStream);
        if (partnerVideo.current) {
          partnerVideo.current.srcObject = remoteStream;
        }
      });
      peerRef.current = peer;
    } catch (err) {
      console.error("Error accessing media devices:", err);
    }
  };

  // Step 3: Add the nextPartner function.
  const nextPartner = () => {
    setPartnerId(null);
    setCommonInterests([]);
    setPartnerStream(null);

    if (peerRef.current) {
      peerRef.current.destroy();
      peerRef.current = null;
    }

    socket.emit("nextPartner");
  };

  return (
    <div className="container text-center mt-5">
      <h2 className="mb-4">Omegle Clone - Interest Matching</h2>

      {!partnerId ? (
        <div className="card p-4 shadow">
          <input
            type="text"
            value={interests}
            onChange={(e) => setInterests(e.target.value)}
            placeholder="Enter interests (comma-separated)"
            className="form-control"
          />
          <button
            onClick={findPartner}
            disabled={searching}
            className="btn btn-primary mt-3"
          >
            {searching ? "Searching..." : "Find a Partner"}
          </button>
          {searching && (
            <p className="text-muted mt-2">Looking for a chat partner...</p>
          )}
        </div>
      ) : (
        <>
          <h4 className="mt-4">Common Interests:</h4>
          <p className="text-muted">
            {commonInterests.length > 0 ? commonInterests.join(", ") : "No common interests"}
          </p>

          <div className="row mt-4">
            <div className="col-md-6">
              <h5>Your Video</h5>
              <video
                ref={userVideo}
                autoPlay
                playsInline
                className="w-100 border border-primary rounded"
              />
            </div>
            <div className="col-md-6">
              <h5>Partner's Video</h5>
              <video
                ref={partnerVideo}
                autoPlay
                playsInline
                className="w-100 border border-danger rounded"
              />
            </div>
          </div>

          <div className="d-flex justify-content-center gap-3 mt-4">
            <button onClick={() => window.location.reload()} className="btn btn-danger">
              Disconnect
            </button>
            <button onClick={nextPartner} className="btn btn-warning">
              Next
            </button>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
