import React, { useState, useEffect, useRef } from "react";
import io from "socket.io-client";
import SimplePeer from "simple-peer";
import "bootstrap/dist/css/bootstrap.min.css";

const socket = io("https://server-crimson-wildflower-4430.fly.dev");

function App() {
  const [partnerId, setPartnerId] = useState(null);
  const [searching, setSearching] = useState(false);
  const [interests, setInterests] = useState("");
  const [commonInterests, setCommonInterests] = useState([]);
  const [stream, setStream] = useState(null);
  const userVideo = useRef();
  const partnerVideo = useRef();
  const peerRef = useRef(null);

  useEffect(() => {
    socket.on("connect", () => {});

    socket.on("partnerFound", ({ partnerId, commonInterests }) => {
      setPartnerId(partnerId);
      setCommonInterests(commonInterests);
      setSearching(false);
    });

    socket.on("signal", ({ signal, from }) => {
      if (peerRef.current) {
        peerRef.current.signal(signal);
      }
    });

    socket.on("partnerDisconnected", () => {
      if (peerRef.current) {
        peerRef.current.destroy();
        peerRef.current = null;
      }
      setPartnerId(null);
      setCommonInterests([]);
    });

    return () => {
      socket.off("partnerFound");
      socket.off("signal");
      socket.off("partnerDisconnected");
    };
  }, []);

  useEffect(() => {
    if (partnerId) {
      startVideoChat();
    }
  }, [partnerId]);

  const startVideoChat = async () => {
    try {
      const userStream = await navigator.mediaDevices.getUserMedia({
        video: true,
        audio: true,
      });
      setStream(userStream);
      if (userVideo.current) userVideo.current.srcObject = userStream;

      const peer = new SimplePeer({
        initiator: true,
        trickle: false,
        stream: userStream,
        config: {
          iceServers: [
            { urls: "stun:stun.l.google.com:19302" },
            { urls: "stun:stun1.l.google.com:19302" },
            {
              urls: "turn:global.turn.twilio.com:3478?transport=udp",
              username: "YOUR_TWILIO_USERNAME",
              credential: "YOUR_TWILIO_CREDENTIAL",
            },
          ],
        },
      });

      peer.on("signal", (signal) => {
        socket.emit("signal", { partnerId, signal });
      });

      peer.on("stream", (remoteStream) => {
        if (partnerVideo.current) {
          partnerVideo.current.srcObject = remoteStream;
        }
      });

      peerRef.current = peer;
    } catch (err) {
      console.error("Error accessing media devices:", err);
    }
  };

  const findPartner = () => {
    setSearching(true);
    const interestList = interests.split(",").map((i) => i.trim().toLowerCase());
    socket.emit("findPartner", interestList);
  };

  return (
    <div className="container text-center mt-5">
      <h2>Link UP - Video Chat</h2>
      {!partnerId ? (
        <div>
          <input value={interests} onChange={(e) => setInterests(e.target.value)} placeholder="Enter interests" />
          <button onClick={findPartner} disabled={searching}>{searching ? "Searching..." : "Find Partner"}</button>
        </div>
      ) : (
        <>
          <video ref={userVideo} autoPlay playsInline />
          <video ref={partnerVideo} autoPlay playsInline />
        </>
      )}
    </div>
  );
}

export default App;
